const a={sendSMS:async(e,n)=>(console.log(`[SIMULATION] Sending SMS to ${e}: ${n}`),!0),sendEmail:async(e,n,o)=>(console.log(`[SIMULATION] Sending Email to ${e} | Subject: ${n} | Body: ${o}`),!0),notifyOrderUpdate:async(e,n,o)=>{const t=`Your order #${n} has been updated to: ${o}`;e.phone&&await a.sendSMS(e.phone,t),e.email&&await a.sendEmail(e.email,`Order Update #${n}`,t)}};export{a as NotificationService};
